import React, { Component } from "react";

class Counter extends Component {
  render() {
    return (
      <div>
        <span>{this.props.item.itemname}: </span>
        <button onClick={this.handleDecrement} className="btn btn-secondary btn-sm m-2">
          -
        </button>
        <span>{this.props.item.value}</span>
        <button onClick={this.handleIncrement} className="btn btn-secondary btn-sm m-2">
          +
        </button>
      </div>
    );
  }

  handleIncrement = () => {
    this.props.onIncrement(this.props.item);
  };

  handleDecrement = () => {
    this.props.onDecrement(this.props.item); // เพิ่มฟังก์ชัน onDecrement
  };
}

export default Counter;
