import { Component } from "react";

class Counter extends Component{
    state = {count: 0};
    
    reder() {
        return (
        <div className="d-inline">
            <p className="d-inline m-2">Counter Component {this.formatCount()}</p>
            <button 
            className="btn btn-primary d-inline m-2"
            onClick={this.increment}> + </button>
            <button 
            className="btn btn-primary d-inline m-2"
            onClick={this.reduce}> - </button>
        </div>
        );    
    };
    increment = () => {
        this.state.count += 1;
        this.setState({ count : this.state.count });
        //console.log(this.state.count);
    }
    reduce = () => {
        this.state.count -= 1;
        this.setState({ count : this.state.count });
        //console.log(this.state.count);
    }

    formatCount(){
        return this.state.count == 0 ? <b>Zero</b> : this.state.counr;
    }
}

export default Counter;
