import React, { Component } from "react";
import Counter from "./Counter";

class CounterList extends Component {
    state = {
        itemlist: [
            { id: 1, value: 0, itemname: "iPhone", price: 150 },
            { id: 2, value: 0, itemname: "iPad", price: 200 },
            { id: 3, value: 0, itemname: "iMac", price: 300 },
            { id: 4, value: 0, itemname: "iBook", price: 500 },
        ],
    };

    Increment = (item) => {
        this.setState((prevState) => ({
            itemlist: prevState.itemlist.map((i) => (i.id === item.id ? { ...i, value: i.value + 1 } : i)),
        }));
    };

    Decrement = (item) => {
        this.setState((prevState) => ({
            itemlist: prevState.itemlist.map((i) => (i.id === item.id && i.value > 0 ? { ...i, value: i.value - 1 } : i)),
        }));
    };

    resetButton = () => {
        this.setState({
            itemlist: this.state.itemlist.map((item) => ({ ...item, value: 0 })),
        });
    };

    calculateTotal = () => {
        return this.state.itemlist.reduce((total, item) => total + item.value * item.price, 0);
    };

    render() {
        const { itemlist } = this.state;

        return (
            <div>
                <div className="d-inline mx-2">ราคารวม {this.calculateTotal()}</div>
                <button
                    onClick={this.resetButton}
                    className="btn btn-sm btn-warning d-inline"
                >
                    Reset
                </button>
                {itemlist.map((item) => (
                    <Counter
                        key={item.id}
                        item={item}
                        onIncrement={this.Increment}
                        onDecrement={this.Decrement}
                    />
                ))}
            </div>
        );
    }
}

export default CounterList;
