// CounterList.js
import React, { Component } from "react";
import Counter from "./Counter";

class CounterList extends Component {
  state = {
    itemlist: [
      { id: 1, value: 1, itemname: "iPhone", price: 150 },
      { id: 2, value: 1, itemname: "iPad", price: 200 },
      { id: 3, value: 1, itemname: "iMac", price: 300 },
      { id: 4, value: 1, itemname: "iBook", price: 500 },
    ],
    totalPrice: 0, // เพิ่ม state สำหรับราคารวมทั้งหมด
  };

  render() {
    return (
      <div>
        <div>ราคารวมทั้งหมด: {this.state.totalPrice}</div> {/* แสดงผลราคารวมทั้งหมด */}
        {this.state.itemlist.map((item) => {
          return (
            <Counter
              key={item.id}
              item={item}
              onIncrement={this.incrementItem}
              onDecrement={this.decrementItem}
            />
          );
        })}
      </div>
    );
  }

  incrementItem = (item) => {
    const updatedItemList = [...this.state.itemlist];
    const indexItem = updatedItemList.findIndex((i) => i.id === item.id);
    updatedItemList[indexItem] = { ...item };
    updatedItemList[indexItem].value++;
    this.setState({ itemlist: updatedItemList }, this.updateTotalPrice);
  };

  decrementItem = (item) => {
    const updatedItemList = [...this.state.itemlist];
    const indexItem = updatedItemList.findIndex((i) => i.id === item.id);
    updatedItemList[indexItem] = { ...item };
    if (updatedItemList[indexItem].value > 0) {
      updatedItemList[indexItem].value--;
    }
    this.setState({ itemlist: updatedItemList }, this.updateTotalPrice);
  };

  updateTotalPrice() {
    const total = this.state.itemlist.reduce(
      (accumulator, item) => accumulator + item.value * item.price,
      0
    );
    this.setState({ totalPrice: total }); // อัปเดต state ของราคารวมทั้งหมด
  }
}

export default CounterList;
