import { Component } from "react";

class Counter extends Component{
    state = {count: 0};
    
    reder() {
        return (
        <div >
            <h1>Counter Component {this.state.count}</h1>
            <button className="btn btnprimary">add</button>
        </div>
        );    
    };

    formatCount(){
        return this.state.count == 0 ? <h4>Zero</h4> : this.state.counr;
    }
}

export default Counter;
