import React, { Component } from "react";

class Counter extends Component {
  render() {
    return (
      <div className="m-2">
        <span className="badge badge-primary m-2">{this.props.item.itemname}</span>
        <span className="badge badge-secondary m-2">{this.props.item.value}</span>
        <button
          onClick={() => this.props.onIncrement(this.props.item)}
          className="btn btn-secondary btn-sm m-2"
        >
          Increment
        </button>
        <button
          onClick={() => this.props.onDecrement(this.props.item)} // เพิ่มคำสั่งลดที่นี่
          className="btn btn-secondary btn-sm m-2"
        >
          Decrement
        </button>
        <button
          onClick={() => this.props.onDelete(this.props.item.id)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
      </div>
    );
  }
}

export default Counter;
