import React, { Component } from "react";
import Counter from "./Counter";

class CounterList extends Component {
  state = {
    itemlist: [
      { id: 1, value: 1, itemname: "iPhone", price: 150 },
      { id: 2, value: 1, itemname: "iPad", price: 200 },
      { id: 3, value: 1, itemname: "iMac", price: 300 },
      { id: 4, value: 1, itemname: "iBook", price: 500 },
    ],
  };

  render() {
    return (
      <div>
        <div className="d-inline mx-2">ราคารวม 0</div>
        <button
          onClick={this.resetButton}
          className="btn btn-sm btn-warning d-inline"
        >
          Reset
        </button>
        {/* เพิ่มปุ่มที่มีชื่อ "- ให้หน่อยสิ chat" */}
        <button
          onClick={this.chatButton}
          className="btn btn-sm btn-primary d-inline mx-2"
        >
          - ให้หน่อยสิ chat
        </button>
        {this.state.itemlist.map((item) => {
          return <Counter key={item.id} item={item} onIncrement={this.Increment} />;
        })}
      </div>
    );
  }

  Increment = (item) => {
    var _itemList = [...this.state.itemlist];
    const indexItem = _itemList.indexOf(item);
    _itemList[indexItem] = { ...item };
    _itemList[indexItem].value++;
    this.setState({ itemlist: _itemList });
  };

  resetButton = () => {
    var resetItem = this.state.itemlist.map((item) => {
      item.value = 0;
      return item;
    });
    this.setState({ resetItem });
  };

  // เพิ่มฟังก์ชันสำหรับปุ่ม "- ให้หน่อยสิ chat"
  chatButton = () => {
    // ทำสิ่งที่คุณต้องการเมื่อคลิกปุ่ม "- ให้หน่อยสิ chat" ตรงนี้
    console.log("- ให้หน่อยสิ chat ถูกคลิก");
  };
}

export default CounterList;
