import { Component } from "react";

class Counter extends Component{
    state = {count: 0};
    
    reder() {
        return (
        <div className="d-inline">
            <p className="d-inline m-2">Counter Component {this.formatCount()}</p>
            <button className="btn btnprimary d-inline m-2">add</button>
        </div>
        );    
    };

    formatCount(){
        return this.state.count == 0 ? <b>Zero</b> : this.state.counr;
    }
}

export default Counter;
