import { Component } from "react";

class Counter extends Component{

    state = {count: this.props.value,
             itemname 
    };

    render(){
        return (
        <div className="d-inline">
            <p className="d-inline m-2">Counter Component {this.formatCount()}</p>
            <button 
                className="btn btn-primary btn-sm d-inline m-2"
                onClick={this.increment}>Add</button>
                <button 
                className="btn btn-primary btn-sm d-inline m-2"
                onClick={this.reduce}>Delete</button>
            
        </div>
        );
    };

    increment = () => {
        this.state.count += 1;
        this.setState({ count : this.state.count });
        //console.log(this.state.count);
    }

    reduce = () => {
        this.state.count -= 1;
        this.setState({ count : this.state.count });
        //console.log(this.state.count);
    }

    formatCount(){
        return this.state.count == 0 ? <b>Zero</b> : this.state.count;
    }
}



export default Counter;
