// ...
class CounterList extends Component {
    // ...
  
    // เพิ่มฟังก์ชัน onDecrement
    onDecrement = (item) => {
      const _itemList = [...this.state.itemlist];
      const indexItem = _itemList.indexOf(item);
  
      if (_itemList[indexItem].value > 0) {
        _itemList[indexItem] = { ...item };
        _itemList[indexItem].value--;
        this.setState({ itemlist: _itemList });
      }
    };
  
    // ...
  
    render() {
      return (
        <div>
          <div className="d-inline mx-2">ราคารวม </div>
          <button onClick={this.resetButton} className="btn btn-sm btn-warning d-inline">
            Reset
          </button>
          {this.state.itemlist.map((item) => {
            return (
              <Counter
                key={item.id}
                item={item}
                onIncrement={this.Increment}
                onDecrement={this.onDecrement} // ส่งฟังก์ชัน onDecrement ไปยัง Counter
              />
            );
          })}
        </div>
      );
    }
    // ...
  }
